import { Component, Input, OnInit } from '@angular/core';
import { BookService } from '../rick-morty.service';
import { ActivatedRoute, Router } from '@angular/router';
import { DomSanitizer, SafeUrl } from '@angular/platform-browser';
@Component({
  selector: 'app-user-books',
  templateUrl: './user-books.component.html',
  styleUrls: ['./user-books.component.css']
})
export class UserBooksComponent implements OnInit{

  user: any;

  constructor(private _router: Router,private _route: ActivatedRoute,
    private bookService: BookService, private sanitizer: DomSanitizer) { }

  ngOnInit(): void {
    let id_user = this._route.snapshot.paramMap.get('id');
    this.bookService.getUserByID(id_user).subscribe(
      (response) => {
        this.user = response;
      },
      (error) => {
        console.log('Error al cargar datos');
      }
    );
  }

  getBase64ImageSrc(base64Image: string): SafeUrl {
    const imageUrl = `data:image/jpg;base64,${this.user.profile_image}`;
    return this.sanitizer.bypassSecurityTrustUrl(imageUrl);
  }

  onBack(): void{
    this._router.navigate(['/home']);
  }
}
<section class="container" *ngIf="user">
  <div class="row">


    <div class="col">
    </div>

    <div class="col">
      <div class="card">
        <div class="card-body">
          <h5 class="card-title">{{user.username}}</h5>
          <p class="card-text">
            <strong>Author</strong>: {{user.email}}
            <br>

          </p>
          <!--<img [src]="'data:image/jpg;base64,' + user.profile_image"
          height="300px" width="300px" class="card-img-top" alt="imagen del libro"/>
          <button class="btn btn-danger" (click)="onBack()">Back</button> -->
        </div>
      </div>


    </div>
  </div>
</section>
