import { Component, Input, OnInit } from '@angular/core';
import { BookService } from '../rick-morty.service';
import { ActivatedRoute, Router } from '@angular/router';
import { DomSanitizer, SafeUrl } from '@angular/platform-browser';

@Component({
  selector: 'app-profile-settings',
  templateUrl: './profile-settings.component.html',
  styleUrls: ['./profile-settings.component.css']
})
export class ProfileSettingsComponent  implements OnInit{
  user: any;

  constructor(private _router: Router,private _route: ActivatedRoute,
    private bookService: BookService, private sanitizer: DomSanitizer) { }

  ngOnInit(): void {
    let id_user = this._route.snapshot.paramMap.get('id');
    this.bookService.getBookByID(id_user).subscribe(
      (response) => {
        this.user = response;
      },
      (error) => {
        console.log('Error al cargar datos');
      }
    );
  }

  getBase64ImageSrc(base64Image: string): SafeUrl {
    const imageUrl = `data:image/jpg;base64,${this.user.profile_image}`;
    return this.sanitizer.bypassSecurityTrustUrl(imageUrl);
  }
  postBook(){

  }
}
<section class="container" *ngIf="user">
  <div class="row">


    <div class="col">
    </div>

    <div class="col">
      <div class="form-group">
        <label for="input_title">Title</label>
        <input type="text" value="{{user.username}}" class="form-control" id="imput_title" aria-describedby="emailHelp" placeholder="Enter Title">
      </div>

      <div class="form-author">
        <label for="input_author">Author</label>
        <input type="text" value="{{user.email}}" class="form-control" id="input_author" placeholder="Enter Author">
      </div>

      <div class="form-profile_image">
        <label for="input_prfile_image" class="form-label">Search Image</label>
        <input class="form-control form-control-sm" id="input_profile_image" type="file">
      </div>

      <div>
        <button id="submit_button" class="btn btn-danger"
        [routerLink]="['/book_view',user.id_book]">Submit</button>
      </div>

    </div>
  </div>
</section>
