import { Component, Input, OnInit } from '@angular/core';
import { BookService } from '../rick-morty.service';
import { ActivatedRoute, Router } from '@angular/router';
import { DomSanitizer, SafeUrl } from '@angular/platform-browser';

@Component({
  selector: 'app-book-view',
  templateUrl: './book-view.component.html',
  styleUrls: ['./book-view.component.css']
})
export class BookViewComponent implements OnInit{

  book: any;

  constructor(private _router: Router,private _route: ActivatedRoute,private bookService: BookService, private sanitizer: DomSanitizer) { }

  ngOnInit(): void {
    let id_book = this._route.snapshot.paramMap.get('id');
    this.bookService.getBookByID(id_book).subscribe(
      (response) => {
        this.book = response;
      },
      (error) => {
        console.log('Error al cargar datos');
      }
    );
  }

  getBase64ImageSrc(base64Image: string): SafeUrl {
    const imageUrl = `data:image/jpg;base64,${this.book.image_cover}`;
    return this.sanitizer.bypassSecurityTrustUrl(imageUrl);
  }

  onBack(): void{
    this._router.navigate(['/books']);
  }
}
<section class="container" *ngIf="book">
  <div class="row">


    <div class="col">
      <img [src]="'data:image/jpg;base64,' + book.cover_image"
      height="300px" width="300px" class="card-img-top" alt="imagen del libro"/>
    </div>

    <div class="col">
      <div class="card">
        <div class="card-body">
          <h5 class="card-title">{{book.title | uppercase }}</h5>
          <p class="card-text">
            <strong>Author</strong>: {{book.author}}
            <br>
            <strong>ISBN</strong>: {{book.isbn}}
            <br>
            <strong>Category</strong>: {{book.category}}
            <br>
          </p>
          <img [src]="'data:image/jpg;base64,' + book.image_cover" />
          <button class="btn btn-danger" (click)="onBack()">Back</button>
        </div>
      </div>
    </div>
  </div>
</section>
