import { HttpClient } from '@angular/common/http';
import { Component, OnInit } from '@angular/core';
import { BookService } from '../rick-morty.service';

@Component({
  selector: 'app-books',
  templateUrl: './books.component.html',
  styleUrls: ['./books.component.css'],
})
export class BookComponent implements OnInit {
  books: any;

  constructor(private bookService: BookService) {}

  ngOnInit() {
    this.loadRndomBooks();
  }

  getRndom() {
    return Math.floor(Math.random() * 5 + 1);
  }

  loadRndomBooks() {
    this.bookService.get8RndomBooks().subscribe(
      (response) => {
        this.books = response;
      },
      (error) => {
        console.log('Error al cargar datos');
      }
    );
  }
}
<body>asdsad</body>

<div class="row">
  <div class="columna" class="col-sm-6 col-md-3" *ngFor="let book of books; let i = index">
    <div class="card">
      <img src="{{book.coverImage}}" height="300px" width="300px" class="card-img-top" alt="imagen del character">
      <div class="card-body">
        <h5 class="card-title">{{book.title | uppercase}}</h5>
        <p class="card-text"><strong>ID</strong>: {{book.id}}</p>
        <button class="btn btn-danger" [routerLink]="['/book',book.id]">See more</button>
      </div>
    </div>
  </div>
</div>
