import { Component, Input, OnInit } from '@angular/core';
import { BookService } from '../rick-morty.service';
import { ActivatedRoute, Router } from '@angular/router';

@Component({
  selector: 'app-book-view',
  templateUrl: './book-view.component.html',
  styleUrls: ['./book-view.component.css']
})
export class BookViewComponent implements OnInit{

  book: any;

  constructor(private _router: Router,private _route: ActivatedRoute,private bookService: BookService) { }

  ngOnInit(): void {
    let id_book = this._route.snapshot.paramMap.get('id');
    this.bookService.getBookByID(id_book).subscribe(
      (response) => {
        this.book = response;
      },
      (error) => {
        console.log('Error al cargar datos');
      }
    );
  }
  onBack(): void{
    this._router.navigate(['/books']);
  }
}
<section class="container" *ngIf="book">
  <div class="row">
    <div class="col">
      <img src="{{book.cover_image}}" alt="imagen de {{book.title}}">
    </div>

    <div class="col">
      <img [src]="getBase64ImageSrc(book.cover_image)" alt="imagen de {{book.title}}">
    </div>

    <div class="col">
      <div class="card">
        <div class="card-body">
          <h5 class="card-title">{{book.title | uppercase }}</h5>
          <p class="card-text">
            <strong>Author</strong>: {{book.author}}
            <br>
            <strong>ISBN</strong>: {{book.isbn}}
            <br>
            <strong>Category</strong>: {{book.category}}
            <br>
          </p>
          <img [src]="'data:image/jpg;base64,' + book.image_cover" />
          <button class="btn btn-danger" (click)="onBack()">Back</button>
        </div>
      </div>
    </div>
  </div>
</section>
