import { Component, Input, OnInit } from '@angular/core';
import { BookService } from '../rick-morty.service';
import { ActivatedRoute, Router } from '@angular/router';
import { DomSanitizer, SafeUrl } from '@angular/platform-browser';

@Component({
  selector: 'app-edit-books',
  templateUrl: './edit-books.component.html',
  styleUrls: ['./edit-books.component.css']
})
export class EditBooksComponent implements OnInit{
  book: any;

  constructor(private _router: Router,private _route: ActivatedRoute,private bookService: BookService, private sanitizer: DomSanitizer) { }

  title ="";
  author ="";
  isbn ="";
  category ="";
  cover_image = null;

  ngOnInit(): void {
    let id_book = this._route.snapshot.paramMap.get('id');
    this.bookService.getBookByID(id_book).subscribe(
      (response) => {
        this.book = response;
      },
      (error) => {
        console.log('Error al cargar datos');
      }
    );
  }

  getBase64ImageSrc(base64Image: string): SafeUrl {
    const imageUrl = `data:image/jpg;base64,${this.book.image_cover}`;
    return this.sanitizer.bypassSecurityTrustUrl(imageUrl);
  }

  onBack(): void{
    this._router.navigate(['/books']);
  }

  setLabels(){
    this.author = this.book.author;
  }
}
<section class="container" *ngIf="book">
  <div class="row">


    <div class="col">
    </div>

    <div class="col">
      <div class="form-group">
        <label for="input_title">Title</label>
        <input class="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" placeholder="Enter Title">
      </div>

      <div class="form-author">
        <label for="input_author">Author</label>
        <input type="text" value="{{book.author}}" class="form-control" id="exampleInputPassword1" placeholder="Enter Author" value="{{author}}">
      </div>

      <div class="form-ISBN">
        <label for="input_ISBN">ISBN</label>
        <input type="text" value="{{book.isbn}}" class="form-control" id="input_ISBN" placeholder="Enter ISBN">
      </div>

      <div class="form-category">
        <label for="input_category">Category</label>
        <input type="text" value="{{book.category}}" class="form-control" id="input_category" placeholder="Enter Category">
      </div>

      <div class="form-cover_image">
        <label for="input_cover_image" class="form-label">Search Image</label>
        <input class="form-control form-control-sm" id="input_cover_image" type="file">
      </div>

      <div>
        <button id="submit_button" class="btn btn-danger"
        [routerLink]="['/book_view',book.id_book]">Submit</button>
      </div>

    </div>
  </div>
</section>
